import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';

// Components
import Dashboard from './components/Dashboard';
import OnboardingFlow from './components/OnboardingFlow';
import MealLogForm from './components/MealLogForm';
import BadgeCollection from './components/BadgeCollection';
import SwapModal from './components/SwapModal';

// Services
import { apiService } from './services/api';

// Types
import { User, SwapRecommendation } from './types';

interface OnboardingData {
  name: string;
  monthlyGoal: string;
  dietaryPreferences: string[];
  motivations: string[];
}

function App() {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [showMealForm, setShowMealForm] = useState(false);
  const [showSwapModal, setShowSwapModal] = useState(false);
  const [swapRecommendations, setSwapRecommendations] = useState<SwapRecommendation[]>([]);
  const [selectedMealId, setSelectedMealId] = useState<number>(0);
  const [originalFood, setOriginalFood] = useState<string>('');

  // Check if user is logged in on app start
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('access_token');
      if (token) {
        try {
          const userData = await apiService.getCurrentUser();
          setUser(userData);
        } catch (error) {
          localStorage.removeItem('access_token');
        }
      }
      setIsLoading(false);
    };

    checkAuth();
  }, []);

  const handleOnboardingComplete = async (onboardingData: OnboardingData) => {
    try {
      // Map dietary preferences to valid type
      const getDietaryPreference = (preferences: string[]): 'omnivore' | 'vegetarian' | 'vegan' | 'pescatarian' => {
        const validPreferences = ['omnivore', 'vegetarian', 'vegan', 'pescatarian'] as const;
        const firstPreference = preferences[0]?.toLowerCase();
        return validPreferences.includes(firstPreference as any) ? firstPreference as any : 'omnivore';
      };

      // Register user with onboarding data
      const authResponse = await apiService.register({
        email: `${onboardingData.name.toLowerCase().replace(/\s+/g, '')}@greenflow.temp`,
        password: 'temp123', // In real app, get this from user
        name: onboardingData.name,
        dietary_preference: getDietaryPreference(onboardingData.dietaryPreferences)
      });

      // Get user data
      const userData = await apiService.getCurrentUser();
      setUser(userData);
    } catch (error) {
      console.error('Registration failed:', error);
      // For demo purposes, create a mock user
      setUser({
        id: 1,
        email: `${onboardingData.name.toLowerCase()}@demo.com`,
        name: onboardingData.name,
        dietary_preference: 'omnivore',
        target_carbon_reduction: 20
      });
    }
  };

  const handleLogout = () => {
    apiService.logout();
    setUser(null);
  };

  const openSwapModal = async (mealId: number, foodName: string) => {
    try {
      const recommendations = await apiService.getSwapRecommendations(mealId);
      setSwapRecommendations(recommendations.recommendations || []);
      setSelectedMealId(mealId);
      setOriginalFood(foodName);
      setShowSwapModal(true);
    } catch (error) {
