import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle, Target, Utensils, ArrowRight, Sparkles } from 'lucide-react';
import toast from 'react-hot-toast';

interface OnboardingFlowProps {
  onComplete: (userData: OnboardingData) => void;
}

interface OnboardingData {
  name: string;
  monthlyGoal: string;
  dietaryPreferences: string[];
  motivations: string[];
}

interface WelcomeScreenProps {
  onNext: (name: string) => void;
}

interface GoalSettingScreenProps {
  name: string;
  onNext: (goal: string) => void;
  onPrev: () => void;
}

interface DietaryPreferenceScreenProps {
  name: string;
  goal: string;
  onComplete: (preferences: string[], motivations: string[]) => void;
  onPrev: () => void;
}

const WelcomeScreen: React.FC<WelcomeScreenProps> = ({ onNext }) => {
  const [name, setName] = useState('');

  const handleNext = () => {
    if (name.trim()) {
      onNext(name.trim());
    } else {
      toast.error('Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî! üòä');
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -50 }}
      className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-green-50 to-blue-50 p-6"
    >
      <motion.div
        initial={{ scale: 0 }}
        animate={{ scale: 1 }}
        transition={{ delay: 0.2, type: "spring" }}
        className="mb-8"
      >
        <div className="w-24 h-24 bg-green-500 rounded-full flex items-center justify-center text-white text-4xl">
          üå±
        </div>
      </motion.div>

      <motion.h1
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
        className="text-4xl font-bold text-gray-800 mb-4 text-center"
      >
        Greenflow LifeÏóê 
        <br />
        <span className="text-green-600">Ïò§Ïã† Í≤ÉÏùÑ ÌôòÏòÅÌï¥Ïöî!</span>
      </motion.h1>

      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.6 }}
        className="text-lg text-gray-600 mb-8 text-center max-w-md"
      >
        ÎßõÏûàÎäî ÏùåÏãùÍ≥º Ìï®Íªò ÏßÄÍµ¨Î•º ÏßÄÌÇ§Îäî Ïó¨Ï†ïÏùÑ ÏãúÏûëÌï¥Î≥¥ÏÑ∏Ïöî. 
        ÏûëÏùÄ ÏÑ†ÌÉùÏù¥ ÌÅ∞ Î≥ÄÌôîÎ•º ÎßåÎì≠ÎãàÎã§! üåç
      </motion.p>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.8 }}
        className="w-full max-w-md"
      >
        <input
          type="text"
          placeholder="Ïù¥Î¶ÑÏùÑ ÏïåÎ†§Ï£ºÏÑ∏Ïöî"
          value={name}
          onChange={(e) => setName(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleNext()}
          className="w-full px-6 py-4 rounded-xl border-2 border-gray-200 focus:border-green-500 focus:outline-none text-lg text-center"
        />

        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleNext}
          className="w-full mt-6 bg-green-500 hover:bg-green-600 text-white font-semibold py-4 px-6 rounded-xl flex items-center justify-center gap-2 transition-colors"
        >
          ÏãúÏûëÌïòÍ∏∞ <ArrowRight className="w-5 h-5" />
        </motion.button>
      </motion.div>
    </motion.div>
  );
};

const GoalSettingScreen: React.FC<GoalSettingScreenProps> = ({ name, onNext, onPrev }) => {
  const [selectedGoal, setSelectedGoal] = useState<string>('');

  const goals = [
    {
      id: 'beginner',
      title: 'üå± ÏßÄÍµ¨ ÏπúÌôîÏ†Å ÏãúÏûë',
      description: 'ÌÉÑÏÜå Î∞úÏûêÍµ≠ÏùÑ Ï§ÑÏù¥Îäî Ï≤´ Í±∏Ïùå',
      target: 'Ïõî 10% ÌÉÑÏÜå Ï†àÏïΩ'
    },
    {
      id: 'intermediate',
      title: 'üåø ÏùòÏãùÏ†ÅÏù∏ ÏÜåÎπÑÏûê',
      description: 'Íæ∏Ï§ÄÌïú ÏπúÌôòÍ≤Ω ÏãùÎã® Ïã§Ï≤ú',
      target: 'Ïõî 25% ÌÉÑÏÜå Ï†àÏïΩ'
    },
    {
      id: 'advanced',
      title: 'üå≥ ÏßÄÍµ¨ ÏßÄÌÇ¥Ïù¥',
      description: 'Ï†ÅÍ∑πÏ†ÅÏù∏ ÌôòÍ≤Ω Î≥¥Ìò∏ Ïã§Ï≤ú',
      target: 'Ïõî 40% ÌÉÑÏÜå Ï†àÏïΩ'
    }
  ];

  const handleNext = () => {
    if (selectedGoal) {
      onNext(selectedGoal);
    } else {
      toast.error('Î™©ÌëúÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî! üéØ');
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: 100 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: -100 }}
      className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-green-50 to-blue-50 p-6"
    >
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-3xl font-bold text-gray-800 mb-2 text-center"
      >
        ÏïàÎÖïÌïòÏÑ∏Ïöî, {name}Îãò! üëã
      </motion.h2>

      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
        className="text-lg text-gray-600 mb-8 text-center max-w-md"
      >
        Ïñ¥Îñ§ Î™©ÌëúÎ°ú ÏãúÏûëÌï¥Î≥¥Í≥† Ïã∂ÏúºÏã†Í∞ÄÏöî?
      </motion.p>

      <div className="w-full max-w-md space-y-4">
        {goals.map((goal, index) => (
          <motion.button
            key={goal.id}
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.3 + index * 0.1 }}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => setSelectedGoal(goal.id)}
            className={`w-full p-6 rounded-xl border-2 text-left transition-all ${
              selectedGoal === goal.id
                ? 'border-green-500 bg-green-50 shadow-md'
                : 'border-gray-200 bg-white hover:border-green-300'
            }`}
          >
            <div className="flex items-center gap-3 mb-2">
              <span className="text-2xl">{goal.title.split(' ')[0]}</span>
              <h3 className="font-semibold text-gray-800">{goal.title.substring(3)}</h3>
            </div>
            <p className="text-gray-600 text-sm mb-2">{goal.description}</p>
            <div className="flex items-center gap-2">
              <Target className="w-4 h-4 text-green-500" />
              <span className="text-green-600 font-medium text-sm">{goal.target}</span>
            </div>
          </motion.button>
        ))}
      </div>

      <div className="flex gap-4 mt-8 w-full max-w-md">
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={onPrev}
          className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-700 font-semibold py-3 px-6 rounded-xl transition-colors"
        >
          Ïù¥Ï†Ñ
        </motion.button>
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleNext}
          className="flex-2 bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-xl flex items-center justify-center gap-2 transition-colors"
        >
          Îã§Ïùå <ArrowRight className="w-5 h-5" />
        </motion.button>
      </div>
    </motion.div>
  );
};

const DietaryPreferenceScreen: React.FC<DietaryPreferenceScreenProps> = ({ 
  name, 
  goal, 
  onComplete, 
  onPrev 
}) => {
  const [selectedPreferences, setSelectedPreferences] = useState<string[]>([]);
  const [selectedMotivations, setSelectedMotivations] = useState<string[]>([]);

  const preferences = [
    { id: 'omnivore', label: 'üçñ Ïû°Ïãù (Î™®Îì† ÏùåÏãù)', icon: 'üçΩÔ∏è' },
    { id: 'reduce_meat', label: 'ü•ó Ïú°Î•ò Ï§ÑÏù¥Îäî Ï§ë', icon: 'üìâ' },
    { id: 'pescatarian', label: 'üêü ÌéòÏä§ÏΩî Î≤†ÏßÄÌÖåÎ¶¨Ïïà', icon: 'üê†' },
    { id: 'vegetarian', label: 'ü•ï Î≤†ÏßÄÌÖåÎ¶¨Ïïà', icon: 'üå±' },
    { id: 'vegan', label: 'üåø ÎπÑÍ±¥', icon: 'üíö' }
  ];

  const motivations = [
    { id: 'environment', label: 'üåç ÌôòÍ≤Ω Î≥¥Ìò∏', desc: 'ÏßÄÍµ¨Î•º ÏúÑÌïú ÏÑ†ÌÉù' },
    { id: 'health', label: 'üí™ Í±¥Í∞ï Í¥ÄÎ¶¨', desc: 'Îçî Í±¥Í∞ïÌïú ÎÇòÎ•º ÏúÑÌï¥' },
    { id: 'cost', label: 'üí∞ Í≤ΩÏ†úÏ†Å Ïù¥Ïú†', desc: 'Í∞ÄÍ≥ÑÎ∂ÄÎ•º ÏÉùÍ∞ÅÌïòÎ©∞' },
    { id: 'curiosity', label: 'ü§î Ìò∏Í∏∞Ïã¨', desc: 'ÏÉàÎ°úÏö¥ ÎèÑÏ†ÑÏùÑ ÏúÑÌï¥' }
  ];

  const togglePreference = (pref: string) => {
    setSelectedPreferences(prev => 
      prev.includes(pref) 
        ? prev.filter(p => p !== pref)
        : [...prev, pref]
    );
  };

  const toggleMotivation = (motivation: string) => {
    setSelectedMotivations(prev => 
      prev.includes(motivation)
        ? prev.filter(m => m !== motivation)
        : [...prev, motivation]
    );
  };

  const handleComplete = () => {
    if (selectedPreferences.length === 0) {
      toast.error('ÏãùÎã® ÏÑ†Ìò∏ÎèÑÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî! üçΩÔ∏è');
      return;
    }
    if (selectedMotivations.length === 0) {
      toast.error('ÎèôÍ∏∞Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî! üí™');
      return;
    }

    onComplete(selectedPreferences, selectedMotivations);
    toast.success('ÌôòÏòÅÌï©ÎãàÎã§! Ìï®Íªò ÏßÄÍµ¨Î•º ÏßÄÏºúÏöî! üåç‚ú®');
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: 100 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: -100 }}
      className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-green-50 to-blue-50 p-6"
    >
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center mb-8"
      >
        <h2 className="text-3xl font-bold text-gray-800 mb-2">
          ÎßàÏßÄÎßâ Îã®Í≥ÑÏòàÏöî! üéØ
        </h2>
        <p className="text-lg text-gray-600">
          {name}ÎãòÏùò ÏãùÎã®Í≥º ÎèôÍ∏∞Î•º ÏïåÎ†§Ï£ºÏÑ∏Ïöî
        </p>
      </motion.div>

      <div className="w-full max-w-md space-y-8">
        {/* ÏãùÎã® ÏÑ†Ìò∏ÎèÑ */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <Utensils className="w-5 h-5" />
            ÏãùÎã® ÏÑ†Ìò∏ÎèÑ
          </h3>
          <div className="space-y-3">
            {preferences.map((pref, index) => (
              <motion.button
                key={pref.id}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.3 + index * 0.1 }}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => togglePreference(pref.id)}
                className={`w-full p-4 rounded-lg border-2 text-left transition-all ${
                  selectedPreferences.includes(pref.id)
                    ? 'border-green-500 bg-green-50'
                    : 'border-gray-200 bg-white hover:border-green-300'
                }`}
              >
                <div className="flex items-center gap-3">
                  <span className="text-2xl">{pref.icon}</span>
                  <span className="font-medium">{pref.label}</span>
                  {selectedPreferences.includes(pref.id) && (
                    <CheckCircle className="w-5 h-5 text-green-500 ml-auto" />
                  )}
                </div>
              </motion.button>
            ))}
          </div>
        </motion.div>

        {/* ÎèôÍ∏∞ */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
        >
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <Sparkles className="w-5 h-5" />
            ÎèôÍ∏∞ (Î≥µÏàò ÏÑ†ÌÉù Í∞ÄÎä•)
          </h3>
          <div className="space-y-3">
            {motivations.map((motivation, index) => (
              <motion.button
                key={motivation.id}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.5 + index * 0.1 }}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => toggleMotivation(motivation.id)}
                className={`w-full p-4 rounded-lg border-2 text-left transition-all ${
                  selectedMotivations.includes(motivation.id)
                    ? 'border-green-500 bg-green-50'
                    : 'border-gray-200 bg-white hover:border-green-300'
                }`}
              >
                <div className="flex items-center gap-3">
                  <div>
                    <div className="font-medium">{motivation.label}</div>
                    <div className="text-sm text-gray-500">{motivation.desc}</div>
                  </div>
                  {selectedMotivations.includes(motivation.id) && (
                    <CheckCircle className="w-5 h-5 text-green-500 ml-auto" />
                  )}
                </div>
              </motion.button>
            ))}
          </div>
        </motion.div>
      </div>

      <div className="flex gap-4 mt-8 w-full max-w-md">
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={onPrev}
          className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-700 font-semibold py-3 px-6 rounded-xl transition-colors"
        >
          Ïù¥Ï†Ñ
        </motion.button>
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={handleComplete}
          className="flex-2 bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-xl flex items-center justify-center gap-2 transition-colors"
        >
          ÏôÑÎ£å! <Sparkles className="w-5 h-5" />
        </motion.button>
      </div>
    </motion.div>
  );
};

export const OnboardingFlow: React.FC<OnboardingFlowProps> = ({ onComplete }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [userData, setUserData] = useState<Partial<OnboardingData>>({});

  const handleWelcomeNext = (name: string) => {
    setUserData(prev => ({ ...prev, name }));
    setCurrentStep(2);
  };

  const handleGoalNext = (goal: string) => {
    setUserData(prev => ({ ...prev, monthlyGoal: goal }));
    setCurrentStep(3);
  };

  const handleComplete = (preferences: string[], motivations: string[]) => {
    const finalData: OnboardingData = {
      name: userData.name!,
      monthlyGoal: userData.monthlyGoal!,
      dietaryPreferences: preferences,
      motivations: motivations
    };
    onComplete(finalData);
  };

  return (
    <div className="relative overflow-hidden">
      <AnimatePresence mode="wait">
        {currentStep === 1 && (
          <WelcomeScreen key="welcome" onNext={handleWelcomeNext} />
        )}
        {currentStep === 2 && (
          <GoalSettingScreen 
            key="goal"
            name={userData.name!}
            onNext={handleGoalNext}
            onPrev={() => setCurrentStep(1)}
          />
        )}
        {currentStep === 3 && (
          <DietaryPreferenceScreen
            key="preferences"
            name={userData.name!}
            goal={userData.monthlyGoal!}
            onComplete={handleComplete}
            onPrev={() => setCurrentStep(2)}
          />
        )}
      </AnimatePresence>
    </div>
  );
};

export default OnboardingFlow;
