import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useForm } from 'react-hook-form';
import { Camera, Plus, Calculator } from 'lucide-react';
import toast from 'react-hot-toast';
import { MealCreate, MealType, CarbonCalculationResponse } from '../types';
import { apiService } from '../services/api';

interface MealLogFormProps {
  onMealLogged?: (meal: any) => void;
}

interface FormData {
  food_name: string;
  portion_size: number;
  meal_type: MealType;
  image_url?: string;
}

const MealLogForm: React.FC<MealLogFormProps> = ({ onMealLogged }) => {
  const [loading, setLoading] = useState(false);
  const [carbonData, setCarbonData] = useState<CarbonCalculationResponse | null>(null);
  const [calculating, setCalculating] = useState(false);

  const { register, handleSubmit, watch, setValue, reset, formState: { errors } } = useForm<FormData>();

  const watchedFoodName = watch('food_name');
  const watchedPortionSize = watch('portion_size');

  const calculateCarbon = async () => {
    if (!watchedFoodName || !watchedPortionSize) {
      toast.error('ÏùåÏãùÎ™ÖÍ≥º ÏñëÏùÑ Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî');
      return;
    }

    try {
      setCalculating(true);
      const result = await apiService.calculateCarbonFootprint({
        food_name: watchedFoodName,
        portion_size: watchedPortionSize
      });
      setCarbonData(result);
      toast.success('ÌÉÑÏÜå Î∞úÏûêÍµ≠Ïù¥ Í≥ÑÏÇ∞ÎêòÏóàÏäµÎãàÎã§!');
    } catch (error) {
      toast.error('ÌÉÑÏÜå Î∞úÏûêÍµ≠ Í≥ÑÏÇ∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    } finally {
      setCalculating(false);
    }
  };

  const onSubmit = async (data: FormData) => {
    try {
      setLoading(true);
      const mealLog = await apiService.createMealLog(data);
      toast.success('ÏãùÏÇ¨Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Í∏∞Î°ùÎêòÏóàÏäµÎãàÎã§! üçΩÔ∏è');
      reset();
      setCarbonData(null);
      onMealLogged?.(mealLog);
    } catch (error) {
      toast.error('ÏãùÏÇ¨ Í∏∞Î°ùÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    } finally {
      setLoading(false);
    }
  };

  const handleImageUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // TODO: Implement Cloudinary upload
      const imageUrl = URL.createObjectURL(file);
      setValue('image_url', imageUrl);
      toast.success('Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏóÖÎ°úÎìúÎêòÏóàÏäµÎãàÎã§!');
    }
  };

  const getSustainabilityColor = (rating: string) => {
    switch (rating) {
      case 'HIGH': return 'text-green-600 bg-green-100';
      case 'MEDIUM': return 'text-yellow-600 bg-yellow-100';
      case 'LOW': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getSustainabilityText = (rating: string) => {
    switch (rating) {
      case 'HIGH': return 'ÏπúÌôòÍ≤Ω';
      case 'MEDIUM': return 'Î≥¥ÌÜµ';
      case 'LOW': return 'Ï£ºÏùò';
      default: return 'Ïïå Ïàò ÏóÜÏùå';
    }
  };

  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-white rounded-xl shadow-lg p-6 max-w-md mx-auto"
    >
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        üçΩÔ∏è Ïò§ÎäòÏùò ÏãùÏÇ¨ Í∏∞Î°ù
      </h2>

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        {/* Food Name */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            ÏùåÏãùÎ™Ö *
          </label>
          <input
            {...register('food_name', { required: 'ÏùåÏãùÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî' })}
            type="text"
            placeholder="Ïòà: ÏÜåÍ≥†Í∏∞ Î∂àÍ≥†Í∏∞"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
          />
          {errors.food_name && (
            <p className="mt-1 text-sm text-red-600">{errors.food_name.message}</p>
          )}
        </div>

        {/* Portion Size */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Ïñë (Í∑∏Îû®) *
          </label>
          <input
            {...register('portion_size', { 
              required: 'ÏñëÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî',
              min: { value: 1, message: '1g Ïù¥ÏÉÅ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî' }
            })}
            type="number"
            placeholder="Ïòà: 200"
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
          />
          {errors.portion_size && (
            <p className="mt-1 text-sm text-red-600">{errors.portion_size.message}</p>
          )}
        </div>

        {/* Meal Type */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            ÏãùÏÇ¨ Ï¢ÖÎ•ò *
          </label>
          <select
            {...register('meal_type', { required: 'ÏãùÏÇ¨ Ï¢ÖÎ•òÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî' })}
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
          >
            <option value="">ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</option>
            <option value="breakfast">ÏïÑÏπ®</option>
